INSERT INTO "blogs"("blog_id", "user_id", "caption", "published", "created_on", "picture_id", "content", "updated_on", "primary_tag", "secondary_tag", "slug", "primary_category", "secondary_category", "description", "url", "snippet", "author_id", "refering_id", "related_id", "note", "like", "comment", "linked_id", "lang") VALUES ('1eltatlvbe', 1, 'teste atewfas et asdf ewr', 't', '2020-10-30 18:04:40.299', NULL, '<h2><strong><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 16pxpx"><span style="color: rgb(48, 81, 157);"><a href="https://pm2.keymetrics.io/docs/usage/pm2-doc-single-page/#welcome" ref="nofollow">Welcome!</a></span></span></span></strong></h2><p><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(37, 37, 37);">Welcome to the PM2 Quick Start!</span></span></span></p><p><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(37, 37, 37);">PM2 is daemon process manager that will help you manage and keep your application online. Getting started with PM2 is straightforward, it is offered as a simple and intuitive CLI, installable via NPM.</span></span></span></p><h2><strong><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 16pxpx"><span style="color: rgb(48, 81, 157);"><a href="https://pm2.keymetrics.io/docs/usage/pm2-doc-single-page/#installation" ref="nofollow">Installation</a></span></span></span></strong></h2><p><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(37, 37, 37);">The latest PM2 version is installable with NPM or Yarn:</span></span></span></p><pre><code>$ npm install pm2@latest -g
# or
$ yarn global add pm2
</code></pre><p><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(37, 37, 37);">To install Node.js and NPM you can use&nbsp;</span></span></span><strong><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(48, 81, 157);"><a href="https://yoember.com/nodejs/the-best-way-to-install-node-js/" ref="nofollow">NVM</a></span></span></span></strong></p><h2><strong><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 16pxpx"><span style="color: rgb(48, 81, 157);"><a href="https://pm2.keymetrics.io/docs/usage/pm2-doc-single-page/#start-an-app" ref="nofollow">Start an app</a></span></span></span></strong></h2><p><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(37, 37, 37);">The simplest way to start, daemonize and monitor your application is by using this command line:</span></span></span></p><pre><code>$ pm2 start app.js
</code></pre><p><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(37, 37, 37);">Or start any other application easily:</span></span></span></p><pre><code>$ pm2 start bashscript.sh
$ pm2 start python-app.py --watch
$ pm2 start binary-file -- --port 1520
</code></pre><p><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(37, 37, 37);">Some options you can pass to the CLI:</span></span></span></p><pre><code># Specify an app name
--name &lt;app_name&gt;

# Watch and Restart app when files change
--watch

# Set memory threshold for app reload
--max-memory-restart &lt;200MB&gt;

# Specify log file
--log &lt;log_path&gt;

# Pass extra arguments to the script
-- arg1 arg2 arg3

# Delay between automatic restarts
--restart-delay &lt;delay in ms&gt;

# Prefix logs with time
--time

# Do not auto restart app
--no-autorestart

# Specify cron for forced restart
--cron &lt;cron_pattern&gt;

# Attach to application log
--no-daemon
</code></pre><p><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 17px"><span style="color: rgb(37, 37, 37);">As you can see many options are available to manage your application with PM2. You will discover them depending on your use case.</span></span></span></p><h2><strong><span style="font-family: Roboto, Arial, sans-serif"><span style="font-size: 16pxpx"><span style="color: rgb(48, 81, 157);"><a href="https://pm2.keymetrics.io/docs/usage/pm2-doc-single-page/#managing-processes" ref="nofollow">Managing processes</a></span></span></span></strong></h2><p></p>', '2020-10-30 18:04:40', NULL, NULL, 'teste-atewfas-et-asdf-ewr', NULL, NULL, NULL, NULL, NULL, 1, NULL, NULL, NULL, 0, 0, NULL, 'en');
